// üîç PARSING JSON + VALIDATION ROBUSTE
// Extraction et validation compl√®te du JSON retourn√© par OpenAI

try {
  // R√©cup√©ration du contenu brut
  const raw = $input.first().json.choices?.[0]?.message?.content || 
              $input.first().json.data || 
              JSON.stringify($input.first().json);

  console.log('üì• Contenu brut re√ßu:', raw.slice(0, 200));

  // Tentative de parsing JSON
  let obj;
  try {
    obj = JSON.parse(raw);
  } catch(parseError) {
    throw new Error(`‚ùå ERREUR PARSING: R√©ponse IA non-JSON valide. Extrait: ${raw.slice(0, 200)}`);
  }

  // V√©rification de la structure principale
  if (!obj.ideas || !Array.isArray(obj.ideas)) {
    throw new Error('‚ùå ERREUR STRUCTURE: Champ "ideas" manquant ou invalide');
  }

  if (obj.ideas.length === 0) {
    throw new Error('‚ùå ERREUR: Tableau "ideas" vide - aucune id√©e g√©n√©r√©e');
  }

  console.log(`‚úÖ ${obj.ideas.length} id√©es d√©tect√©es`);

  // Validation de chaque id√©e
  const requiredFields = [
    'hook', 'flow_steps', 'result', 'outro', 'caption_140', 'voiceover_15s'
  ];

  const validatedIdeas = obj.ideas.map((idea, idx) => {
    // V√©rification des champs obligatoires
    const missingFields = requiredFields.filter(field => !idea[field]);

    if (missingFields.length > 0) {
      throw new Error(
        `‚ùå ERREUR VALIDATION - Id√©e ${idx + 1}: ` +
        `champs manquants ‚Üí ${missingFields.join(', ')}`
      );
    }

    // Validation flow_steps
    if (!Array.isArray(idea.flow_steps)) {
      throw new Error(
        `‚ùå ERREUR - Id√©e ${idx + 1}: ` +
        `flow_steps doit √™tre un tableau, re√ßu: ${typeof idea.flow_steps}`
      );
    }

    if (idea.flow_steps.length < 3) {
      throw new Error(
        `‚ùå ERREUR - Id√©e ${idx + 1}: ` +
        `flow_steps doit contenir min 3 √©l√©ments, re√ßu: ${idea.flow_steps.length}`
      );
    }

    // Validation longueur caption
    if (idea.caption_140.length > 140) {
      console.warn(
        `‚ö†Ô∏è WARNING - Id√©e ${idx + 1}: ` +
        `caption tronqu√©e (${idea.caption_140.length} ‚Üí 140 caract√®res)`
      );
      idea.caption_140 = idea.caption_140.slice(0, 137) + '...';
    }

    // Validation longueur hook
    if (idea.hook.length > 100) {
      console.warn(`‚ö†Ô∏è Hook long pour id√©e ${idx + 1}: ${idea.hook.length} caract√®res`);
    }

    return {
      json: {
        idx: idx + 1,
        ...idea,
        validated: true,
        validation_timestamp: new Date().toISOString(),
        flow_steps_count: idea.flow_steps.length,
        caption_length: idea.caption_140.length,
        voiceover_length: idea.voiceover_15s.length
      }
    };
  });

  // Log de succ√®s
  console.log(`‚úÖ SUCCESS: ${validatedIdeas.length} id√©es valid√©es`);
  console.log('üìä Stats:', {
    total: validatedIdeas.length,
    avg_caption_length: Math.round(
      validatedIdeas.reduce((sum, v) => sum + v.json.caption_length, 0) / validatedIdeas.length
    ),
    avg_flow_steps: Math.round(
      validatedIdeas.reduce((sum, v) => sum + v.json.flow_steps_count, 0) / validatedIdeas.length
    )
  });

  return validatedIdeas;

} catch(error) {
  // Gestion d'erreur robuste
  console.error('üö® ERREUR CRITIQUE PARSING:', error.message);
  console.error('Stack:', error.stack);

  // Retour d'erreur structur√© pour le workflow
  throw new Error(
    `PARSING FAILED: ${error.message}\n\n` +
    `Action requise: V√©rifier le format de r√©ponse de l'Agent IA`
  );
}

// üèÜ S√âLECTION MEILLEURE ID√âE + FALLBACKS ROBUSTES

try {
  // R√©cup√©ration de toutes les id√©es pars√©es
  const ideasNode = $('Parse JSON (id√©es)');
  const ideas = ideasNode.all().map(it => it.json);

  if (!ideas || ideas.length === 0) {
    throw new Error('‚ùå ERREUR: Aucune id√©e disponible pour s√©lection');
  }

  console.log(`üìä D√©but s√©lection sur ${ideas.length} id√©es`);

  // R√©cup√©ration des scores depuis l'API KIE
  const scoredItems = $input.all().map((it, i) => {
    let score = 0;
    let has_api_score = false;
    let api_response_status = 'unknown';

    // Extraction du score avec gestion de multiples structures
    if (it.json?.data?.scores?.[0] !== undefined) {
      score = parseFloat(it.json.data.scores[0]);
      has_api_score = true;
      api_response_status = 'success';
    } else if (it.json?.data?.score !== undefined) {
      score = parseFloat(it.json.data.score);
      has_api_score = true;
      api_response_status = 'success';
    } else if (it.json?.score !== undefined) {
      score = parseFloat(it.json.score);
      has_api_score = true;
      api_response_status = 'success';
    } else if (it.json?.error) {
      console.warn(`‚ö†Ô∏è Erreur API KIE pour id√©e ${i + 1}: ${it.json.error}`);
      api_response_status = 'error';
      score = 0;
    } else {
      console.warn(`‚ö†Ô∏è Structure de r√©ponse inconnue pour id√©e ${i + 1}`);
      api_response_status = 'no_data';
      score = 0;
    }

    return {
      index: i,
      idea_number: i + 1,
      score: isNaN(score) ? 0 : score,
      idea: ideas[i],
      has_api_score,
      api_response_status
    };
  });

  // V√©rification si on a au moins un score valide
  const hasValidScores = scoredItems.some(item => item.has_api_score && item.score > 0);

  if (!hasValidScores) {
    console.warn('‚ö†Ô∏è‚ö†Ô∏è‚ö†Ô∏è FALLBACK ACTIV√â ‚ö†Ô∏è‚ö†Ô∏è‚ö†Ô∏è');
    console.warn('Raison: Aucun score KIE valide d√©tect√©');
    console.warn('Action: S√©lection de la premi√®re id√©e par d√©faut');

    return [{
      json: {
        best: ideas[0],
        score: 0,
        rank: 1,
        total_ideas: ideas.length,
        fallback_used: true,
        fallback_reason: 'API KIE non disponible ou scores invalides',
        selection_method: 'first_by_default',
        timestamp: new Date().toISOString(),
        all_api_statuses: scoredItems.map(s => s.api_response_status)
      }
    }];
  }

  // Tri par score d√©croissant
  scoredItems.sort((a, b) => b.score - a.score);

  // S√©lection de la meilleure id√©e
  const topIdea = scoredItems[0];
  const scores = scoredItems.map(s => s.score);

  // Logs d√©taill√©s
  console.log('‚úÖ‚úÖ‚úÖ S√âLECTION R√âUSSIE ‚úÖ‚úÖ‚úÖ');
  console.log(`üèÜ Meilleure id√©e: #${topIdea.idea_number}`);
  console.log(`üìä Score: ${topIdea.score.toFixed(2)}`);
  console.log(`üìà Tous les scores: [${scores.map(s => s.toFixed(2)).join(', ')}]`);
  console.log(`üìä Stats: min=${Math.min(...scores).toFixed(2)}, max=${Math.max(...scores).toFixed(2)}, avg=${(scores.reduce((a,b) => a+b, 0)/scores.length).toFixed(2)}`);

  return [{
    json: {
      best: topIdea.idea,
      score: topIdea.score,
      rank: 1,
      idea_number: topIdea.idea_number,
      total_ideas: ideas.length,
      score_stats: {
        min: Math.min(...scores),
        max: Math.max(...scores),
        avg: scores.reduce((a,b) => a+b, 0) / scores.length,
        scores_array: scores
      },
      all_scores: scoredItems.map(s => ({
        idea_number: s.idea_number,
        score: s.score,
        api_status: s.api_response_status
      })),
      fallback_used: false,
      selection_method: 'kie_api_scoring',
      timestamp: new Date().toISOString()
    }
  }];

} catch(error) {
  // üö® FALLBACK ULTIME EN CAS D'ERREUR CRITIQUE
  console.error('üö®üö®üö® ERREUR CRITIQUE üö®üö®üö®');
  console.error('Message:', error.message);
  console.error('Stack:', error.stack);

  // Tentative de r√©cup√©ration des id√©es
  try {
    const ideas = $('Parse JSON (id√©es)').all().map(it => it.json);

    if (ideas && ideas.length > 0) {
      console.warn('‚ö†Ô∏è FALLBACK CRITIQUE: Retour de la premi√®re id√©e disponible');

      return [{
        json: {
          best: ideas[0],
          score: 0,
          fallback_used: true,
          fallback_reason: `Erreur critique: ${error.message}`,
          selection_method: 'emergency_fallback',
          total_ideas: ideas.length,
          timestamp: new Date().toISOString(),
          error_details: {
            message: error.message,
            type: error.name
          }
        }
      }];
    }
  } catch(fallbackError) {
    console.error('üö® Impossible de r√©cup√©rer les id√©es:', fallbackError.message);
  }

  throw new Error(
    `S√âLECTION FAILED: ${error.message}\n\n` +
    `Aucune id√©e disponible pour fallback. V√©rifier les nodes en amont.`
  );
}

const best = $input.first().json.best;
