const voiceover = best.voiceover_15s;
const caption = (best.caption_140 + ' ' + hashtags).trim().slice(0, 2200);

const pickBestData = $input.first().json;

return [{ 
  json: { 
    voiceover, 
    caption,
    score: pickBestData.score || 0,
    selection_method: pickBestData.selection_method || 'unknown',
    fallback_used: pickBestData.fallback_used || false,
    idea_number: pickBestData.idea_number || 1
  } 
}];

